,p_process_name=>' - Save Interactive Grid Data'
,p_attribute_01=>'PLSQL_CODE'
,p_attribute_04=>wwv_flow_string.join(wwv_flow_t_varchar2(
'begin  ',
'     case :APEX$ROW_STATUS  ',
'     when ''C'' then -- Note: In EA2 this has been changed from I to C for consistency with Tabular Forms  ',
'         DECLARE kodpromocyjnyid kodpromocyjny.idkodpromocyjny%type;',
'         kawaid kawa.idkawa%type;',
'         kontoid konto.klient_idklient%type;',
'         BEGIN',
'         SELECT idkodpromocyjny INTO kodpromocyjnyid FROM kodpromocyjny WHERE kod=:KOD;',
'         SELECT idkawa INTO kawaid FROM kawa WHERE nazwa=:NAZWA;',
'         SELECT klient_idklient INTO kontoid FROM konto WHERE login=:LOGIN;',
'         insert into zamowienie values (default,kontoid,kawaid,kodpromocyjnyid,:ILOSC,:RAZEM,:DATA_ZAM)',
'         returning idzamowienie into :IDZAMOWIENIE;',
'         END;  ',
'     when ''U'' then  ',
'         DECLARE kodpromocyjnyid kodpromocyjny.idkodpromocyjny%type;',
'         kawaid kawa.idkawa%type;',
'         kontoid konto.klient_idklient%type;',
'         BEGIN',
'         SELECT idkodpromocyjny INTO kodpromocyjnyid FROM kodpromocyjny WHERE kod=:KOD;',
'         SELECT idkawa INTO kawaid FROM kawa WHERE nazwa=:NAZWA;',
'         SELECT klient_idklient INTO kontoid FROM konto WHERE login=:LOGIN;',
'         update zamowienie  ',
'            set  klient_idklient = kontoid,  ',
'                kawa_idkawa = kawaid,',
'                 kodpromocyjny_idkodpromocyjny= kodpromocyjnyid,',
'                 ilosc = :ILOSC,',
'                 razem = :RAZEM,',
'                 data_zam = :DATA_ZAM ',
'          where idzamowienie  = :IDZAMOWIENIE;',
'   END;  ',
'     when ''D'' then  ',
'         delete zamowienie',
'         where idzamowienie = :IDZAMOWIENIE;  ',
'     end case;  ',
'end; '))
,p_attribute_05=>'Y'
,p_attribute_06=>'Y'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
);
